(1527, 151, 'important_include', '<p><span style=\"font-weight: 400;\">* Rates</span><b> do not include 19% VAT (</b><span style=\"font-weight: 400;\">Residents outside of Colombia and foreigners enjoy tax exemption as established in decree 2646 of 2013 that regulates the article) To enjoy this exemption at the time of check in hotels They must present a stamped passport, otherwise they must pay the tax at the hotel.</span><span style=\"font-weight: 400;\"><br /></span><span style=\"font-weight: 400;\">* </span><b>Children:</b><span style=\"font-weight: 400;\"> Rate applies when sharing a bed with their parents and the child\'s consumption is paid at the hotel. If you want a separate bed, you must cancel the adult rate. Maximum 2 children per room.</span><span style=\"font-weight: 400;\"><br /></span><span style=\"font-weight: 400;\">* </span><b>Infants: </b><span style=\"font-weight: 400;\">They only pay Medi</span><b>cal insurance 3 USD / day + air tickets from 2 years old.</b><b><br /></b><b>* </b><span style=\"font-weight: 400;\">Accommodations: DOUBLE rooms indicate if it is twin or matrimonial, it will be assigned in twin if the hotel has availability at check-in, otherwise it will always be a double bed. </span><b>TRIPLE</b><span style=\"font-weight: 400;\"> rooms in double bed + additional and / or sofa bed subject to the type of room that allows it; except in hotels that have rooms with 3 beds.</span><span style=\"font-weight: 400;\"><br /></span><span style=\"font-weight: 400;\">* </span><b>Hotel hours:</b><span style=\"font-weight: 400;\"> Check In 3:00 p.m. - Check out 1:00 p.m.</span><span style=\"font-weight: 400;\"><br /></span><span style=\"font-weight: 400;\">* </span><b>Hotels planned</b><span style=\"font-weight: 400;\">: Take into account at the time of booking, in case of not having availability in the hotels provided, the reservation will be confirmed in hotels of similar category.</span></p>'),
(1528, 151, 'iterary_include', '<h6><b>Day 1: Arrival in Armenia - Lunch - Coffee experience<br /></b></h6><h6><b>Day 2: Excursion to the Cocora Valley, Salento and Filandia with lunch</b></h6><h6><b>Day 3: Transfer to the airport - Flight to Leticia - Lunch - Arrival of the parrots</b></h6><h6><b></b><b>Day 4: Visit to Puerto Nariño - Isla de los micos - pink dolphins - Tarapoto Lakes</b></h6><h6><b></b><b></b><b>Day 5: Transfer to the airport - Flight to Cartagena - Transfer to the hotel - City tour around Cartagena</b><span style=\"font-weight: 400;\"><br /></span></h6><h6><b>Day 6: Free day in Cartagena</b><b><br /></b><span style=\"font-weight: 400;\"><br /></span><b>Day 7: Transfer to the airport of Cartagena - Flight to Bogota.</b></h6><p style=\"text-align: center;\"><span style=\"font-weight: 400;\">***End of our services***</span><span style=\"font-weight: 400;\"><br /></span><span style=\"font-weight: 400;\">You can take extra nights and activities to continue living an unforgettable experience</span></p>'),
(1529, 151, 'tours_include', '<ul><li><span style=\"font-weight: 400;\">Coffee experience in San Alberto</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">Tour to Filandia, Cocora and Salento</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">Visit to Santander Park to see the arrival of parrots</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">Day in Puerto Nariño in regular service</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">Night walk in the jungle</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">City tour through Cartagena, regular service</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">7 day medical assistance card</span></li></ul>'),
(1530, 151, 'download_include', '<p><a href=\"http://new.anditrip.com/wp-content/uploads/2019/03/Colombian-green-Axm-Let-Ctg-7.docx\">Colombian green Axm Let Ctg 7</a></p>'),
(1531, 151, 'no_include_description', '<ul><li><span style=\"font-weight: 400;\">International air ticket</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">Feeding not described</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">Volunteer hotel insurance to pay at the hotel</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">Suggested activities are not included in the program price</span><span style=\"font-weight: 400;\"><br /></span></li><li><span style=\"font-weight: 400;\">Not stipulated or personal expenses and tips</span></li></ul>'),
