(3432, 196, '_card_medical_include', 'field_5b5a31c24583c'),
(3433, 196, 'important_include', '<ul><li><span style=\"font-weight: 400;\">* Rates</span><b> do not include 19% VAT</b><span style=\"font-weight: 400;\"> (Residents outside of Colombia and foreigners enjoy tax exemption as established in decree 2646 of 2013 that regulates the article) To enjoy this exemption at the time of check in hotels They must present a stamped passport, otherwise they must pay the tax at the hotel.</span></li><li><span style=\"font-weight: 400;\">* </span><b>Children:</b><span style=\"font-weight: 400;\"> Rate applies when sharing a bed with their parents and the child\'s consumption is paid at the hotel. If you want a separate bed, you must cancel the adult rate. Maximum 2 children per room.</span></li><li><span style=\"font-weight: 400;\">* </span><b>Infants: </b><span style=\"font-weight: 400;\">They only pay Medical insurance 3 USD / day + air tickets from 2 years old.</span></li><li><span style=\"font-weight: 400;\">* </span><b>Accommodations: DOUBLE</b><span style=\"font-weight: 400;\"> rooms indicate if it is twin or matrimonial, it will be assigned in twin if the hotel has availability at check-in, otherwise it will always be a double bed. </span><b>TRIPLE</b><span style=\"font-weight: 400;\"> rooms in double bed + additional and / or sofa bed subject to the type of room that allows it; except in hotels that have rooms with 3 beds.</span></li><li><span style=\"font-weight: 400;\">* </span><b>Hotel hours:</b><span style=\"font-weight: 400;\"> Check In 3:00 p.m. - Check out 1:00 p.m.</span></li><li><span style=\"font-weight: 400;\">* </span><b>Hotels planned:</b><span style=\"font-weight: 400;\"> Take into account at the time of booking, in case of not having availability in the hotels provided, the reservation will be confirmed in hotels of similar category.</span></li></ul>'),
(3434, 196, '_important_include', 'field_5b5a31e44583d'),
(3435, 196, 'iterary_include', '<h6><b>Day 1 Arrival in Bogota - Welcome and transfer to the hotel.</b></h6><h6><b>Day 2 Visit to the Salt Cathedral - Lunch - City tour around Bogotá with monserrate</b></h6><h6><b>Day 3: Transfer to the airport in Bogota - Flight to Santa Marta - Transfer to the hotel - Lunch - City tour around Santa Marta</b></h6><h6><b>Day 4: Excursion to Tayrona National Park with lunch.</b></h6><h6><b>Day 5: Land displacement from Santa Marta to Cartagena - Lunch - City tour around Cartagena</b></h6><h6><b>Day 6: Free day in Cartagena</b></h6><h6><b>Day 7: Transfer to the airport of Cartagena - Flight to Bogota.</b><b> </b></h6><p><i></i><i> </i><span style=\"font-weight: 400;\">***End of our services***</span> <span style=\"font-weight: 400;\">You can take extra nights and activities to continue living an unforgettable experience</span></p>'),
(3436, 196, '_iterary_include', 'field_5b5a320a4583e'),
(3437, 196, 'tours_include', '<ul><li><span style=\"font-weight: 400;\">Salt Cathedral</span></li><li><span style=\"font-weight: 400;\">City tour in Bogotà with monserrate</span></li><li><span style=\"font-weight: 400;\">City tour in Santa Marta, regular service </span></li><li><span style=\"font-weight: 400;\">Excursion to Tayrona Park, sector to choose between: Cañaveral or Neguanje.</span></li><li><span style=\"font-weight: 400;\">City tour through Cartagena, regular service</span></li></ul>'),
